/* ------------------------------------------------------------------ */
/*  Platform-to-icon mapping                                           */
/* ------------------------------------------------------------------ */

const platformIcons: Record<string, React.ElementType> = {
  github: Github,
  twitter: XTwitterIcon,
  x: XTwitterIcon,
  instagram: Instagram,
  youtube: Youtube,
  linkedin: Linkedin,
  tiktok: TikTokIcon,
  threads: ThreadsIcon,
  facebook: FacebookIcon,
  twitch: Twitch,
  discord: DiscordIcon,
  spotify: SpotifyIcon,
  snapchat: SnapchatIcon,
  pinterest: PinterestIcon,
  reddit: RedditIcon,
  tumblr: TumblrIcon,
  whatsapp: WhatsAppIcon,
  telegram: TelegramIcon,
  bluesky: BlueskyIcon,
  mastodon: MastodonIcon,
  soundcloud: SoundCloudIcon,
  "apple-music": AppleMusicIcon,
  bandcamp: BandcampIcon,
  patreon: PatreonIcon,
  "ko-fi": KoFiIcon,
  cashapp: CashAppIcon,
  venmo: VenmoIcon,
  email: Mail,
  website: Globe,
  custom: Globe,
};

/* ------------------------------------------------------------------ */
/*  Component                                                          */
/* ------------------------------------------------------------------ */

interface SocialIconsRowProps {
  icons: SocialIconRow[];
  textClass?: string;
  className?: string;
  style?: React.CSSProperties;
}

export function SocialIconsRow({ icons, textClass, className, style }: SocialIconsRowProps) {
  if (icons.length === 0) return null;

  return (
    <div className={cn("flex items-center justify-center gap-3 flex-wrap", className)} style={style}>
      {icons.map((icon) => {
        const Icon = platformIcons[icon.platform.toLowerCase()] ?? Globe;
        return (
          <a
            key={icon.id}
            href={icon.url}
            target="_blank"
            rel="noopener noreferrer"
            className={cn(
              "flex h-10 w-10 items-center justify-center rounded-full transition-transform hover:scale-110 opacity-70 hover:opacity-100",
              textClass
            )}
            aria-label={icon.platform}
          >
            <Icon className="h-5 w-5" />
          </a>
        );
      })}
    </div>
  );
}
